sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading || newValue === '') {
      return;
   }

	var cmdbci = g_form.getReference('cmdb_ci', addToDesc);

   //Type appropriate comment here, and begin script below

}



function addToDesc(cmdbci) {
  var tempSD = g_form.getValue('description');
	var d = new Date();
    var timestr = d.toString();
   g_form.setValue('description', tempSD + "\n" + timestr + "\n CI:" + cmdbci.name);
	g_form.addDecoration('cmdb_ci', 'icon-star', 'picked', 'color-green');
  //g_form.save();

}
